import { Component } from '@angular/core';

@Component({
  selector: 'app-connexion',
  templateUrl: './connexion.component.html',
  styleUrls: ['./connexion.component.css']
})
export class ConnexionComponent {
  public showRegister: boolean = false;
  public showConnexion: boolean = true;
  public message:string = "";
  public class:string="";

  changeToLogin() {
    this.showRegister = false;
    this.showConnexion = true;
  }

  changeToRegister() {
    this.showRegister = true;
    this.showConnexion = false;

  }

  postData(methode: any, url = "", data = {}, json = true) {
    /*const response = await fetch(url, {
      method: methode,
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
        "Access-Control-Allow-Origin": "*",
        "Access-Control-Allow-Credentials": true,
      },
      redirect: "follow",
      referrerPolicy: "no-referrer",
      body: JSON.stringify(data),
    });
    if (json) {
      return response.json();
    }
    else {
      return response;
    }*/
  }

  sendConnect(data:any) {
    this.postData("POST", "http://localhost:3000/auth/login",
      { username: data.username, password: data.password })
      .then(elem => {
        var message = document.getElementById("message");
        if (data.access_token) {
          message.className = "messageGood"
          message.innerHTML = "Connexion réussi !"
          localStorage.setItem('tokens', JSON.stringify(data));
          setTimeout(() => {
            document.location.href = "./listUser.html";
          }, 1000);
        } else {
          message.className = "messageBad"
          message.innerHTML = "Connexion failed !"
        }
      });
      console.log(data);
  }

  inscriptionUser(data:any) {
    this.postData("POST", "http://localhost:3000/users",
      { firstname: data.firstname, lastname: data.name, phone: data.phone, email: data.email, password: data.password, type: type }, false)
      .then(elem => {
        console.log("ok");
        var message = document.getElementById("message2")
        if (data.ok) {
          var button = document.getElementById("signupButton");
          button.disabled = true;
          message.className = "messageGood"
          message.innerHTML = "Inscription réussi !"
          setTimeout(() => {
            document.location.href = "./login.html";
          }, 1000);
        } else {
          message.className = "messageBad"
          message.innerHTML = "Inscription failed !"
        }
      });
      console.log(data);
  }


}
